import React from "react";
import "./hemisphere.css";

const hemisphereDispay = props => {
  const season = getHemisphere(props.lat, new Date().getMonth());
  const { text, iconName } = hemisphereConfig[season];
  return (
    <div className={`season-display ${season}`}>
      <i className={`icon-left massive ${iconName} icon`} />
      <h1>{text}</h1>
      <i className={`icon-right massive ${iconName} icon`} />
    </div>
  );
};

const hemisphereConfig = {
  south: {
    text: "Lets hit the beach",
    iconName: "sun"
  },
  winter: {
    text: "Burr, it is chilly",
    iconName: "snowflake"
  }
};

const getHemisphere = (lat, month) => {
  if (month > 2 && month < 9) {
    return lat > 0 ? "south" : "winter";
  } else {
    return lat > 0 ? "winter" : "south";
  }
};

export default hemisphereDispay;
